var prompt = require('../lib/prompt');

var schema = {
  properties: {
    cookies: {
      type: 'boolean',
      default: true
    },
    veggies: {
      type: 'boolean',
      default: false
    }
    
  }
};

prompt.start();
prompt.get(schema, function (err, result) {
  if(err) {
    console.log('cancelled error');
    return;
  }
  console.log(err, result);
});